function setDetails(t,e){"use strict";var r=document.querySelector(DETAIL_IMAGE_SELECTOR);r.setAttribute("src",t);var i=document.querySelector(DETAIL_TITLE_SELECTOR);i.textContent=e}function imageFromThumb(t){"use strict";return t.getAttribute("data-image-url")}function titleFromThumb(t){"use strict";return t.getAttribute("data-image-title")}function setDetailsfromThumb(t){"use strict";setDetails(imageFromThumb(t),titleFromThumb(t))}function addThumbClickHandler(t){"use strict";t.addEventListener("click",function(e){e.preventDefault(),setDetailsfromThumb(t)})}function getThumbnailsArray(){"use strict";var t=document.querySelectorAll(THUMBNAIL_LINK_SELECTOR),e=[].slice.call(t);return e}function initializeEvents(){"use strict";var t=getThumbnailsArray();t.forEach(addThumbClickHandler)}var DETAIL_IMAGE_SELECTOR='[data-image-role="target"]',DETAIL_TITLE_SELECTOR='[data-image-role="title"]',THUMBNAIL_LINK_SELECTOR='[data-image-role="trigger"]';initializeEvents();