function setDetails(e,t){"use strict";var i=document.querySelector(DETAIL_IMAGE_SELECTOR);i.setAttribute("src",e);var r=document.querySelector(DETAIL_TITLE_SELECTOR);r.textContent=t}function imageFromThumb(e){"use strict";return e.getAttribute("data-image-url")}function titleFromThumb(e){"use strict";return e.getAttribute("data-image-title")}function setDetailsfromThumb(e){"use strict";setDetails(imageFromThumb(e),titleFromThumb(e))}function addThumbClickHandler(e){"use strict";e.addEventListener("click",function(t){t.preventDefault(),setDetailsfromThumb(e),showDetails()})}function getThumbnailsArray(){"use strict";var e=document.querySelectorAll(THUMBNAIL_LINK_SELECTOR),t=[].slice.call(e);return t}function hideDetails(){"use strict";document.body.classList.add(HIDDEN_DETAL_CLASS)}function showDetails(){"use strict";var e=document.querySelector(DETAIL_FRAME_SELECTOR);document.body.classList.remove(HIDDEN_DETAL_CLASS),e.classList.add(TINY_EFFECT_CLASS),setTimeout(function(){e.classList.remove(TINY_EFFECT_CLASS)},50)}function addKeyPressHander(){"use strict";document.body.addEventListener("keyup",function(e){e.preventDefault(),console.log(e.keyCode),27===e.keyCode&&hideDetails()})}function initializeEvents(){"use strict";var e=getThumbnailsArray();e.forEach(addThumbClickHandler),addKeyPressHander()}var DETAIL_IMAGE_SELECTOR='[data-image-role="target"]',DETAIL_TITLE_SELECTOR='[data-image-role="title"]',THUMBNAIL_LINK_SELECTOR='[data-image-role="trigger"]',DETAIL_FRAME_SELECTOR='[data-image-role="frame"]',HIDDEN_DETAL_CLASS="hidden-detail",TINY_EFFECT_CLASS="is-tiny",ESC_KEY=27;initializeEvents();